import React from 'react';
import { Container, Row, Col, Button, Card } from 'react-bootstrap';
import { FaEdit, FaSignOutAlt } from 'react-icons/fa'; // أيقونات التعديل وتسجيل الخروج
import 'bootstrap/dist/css/bootstrap.min.css';

const Home = () => {
  // بيانات المستخدم الافتراضية
  const user = {
    fullName: 'John Doe',
    email: 'john@example.com',
    role: 'Admin',
  };

  // إجراء تعديل الملف الشخصي
  const handleEditProfile = () => {
    alert("Edit Profile");
  };

  // إجراء تسجيل الخروج
  const handleLogout = () => {
    alert("Logged out");
  };

  return (
    <div style={{ backgroundColor: '#e9ecef', minHeight: '100vh' }}>
      <Container className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
        <Row className="w-100">
          <Col xs={12} sm={8} md={6} lg={6} className="mx-auto">
            <Card className="shadow-lg rounded p-5" style={{ backgroundColor: '#ffffff' }}>
              <Card.Body>
                <h3 className="text-center mb-4" style={{ color: '#343a40', fontWeight: 'bold' }}>Welcome to Your Dashboard</h3>

                {/* عرض بيانات المستخدم */}
                <div className="mb-4">
                  <h5 className="text-muted"><strong>Full Name:</strong> {user.fullName}</h5>
                  <h5 className="text-muted"><strong>Email:</strong> {user.email}</h5>
                  <h5 className="text-muted"><strong>User Role:</strong> {user.role}</h5>
                </div>

                {/* أزرار التعديل وتسجيل الخروج */}
                <div className="d-flex justify-content-between mt-4">
                  <Button
                    variant="outline-warning"
                    onClick={handleEditProfile}
                    className="d-flex align-items-center px-4 py-2"
                    style={{
                      fontSize: '16px', 
                      borderRadius: '25px',
                      backgroundColor: '#ffc107',
                      border: 'none',
                      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
                      transition: 'background-color 0.3s ease',
                    }}
                    onMouseEnter={(e) => e.target.style.backgroundColor = '#ffb300'}
                    onMouseLeave={(e) => e.target.style.backgroundColor = '#ffc107'}
                  >
                    <FaEdit className="me-2" /> Edit Profile
                  </Button>
                  <Button
                    variant="outline-danger"
                    onClick={handleLogout}
                    className="d-flex align-items-center px-4 py-2"
                    style={{
                      fontSize: '16px', 
                      borderRadius: '25px',
                      backgroundColor: '#dc3545',
                      border: 'none',
                      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
                      transition: 'background-color 0.3s ease',
                    }}
                    onMouseEnter={(e) => e.target.style.backgroundColor = '#c82333'}
                    onMouseLeave={(e) => e.target.style.backgroundColor = '#dc3545'}
                  >
                    <FaSignOutAlt className="me-2" /> Logout
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Home;
