import React, { useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col, Card } from 'react-bootstrap';
import { FaSave } from 'react-icons/fa';
import 'bootstrap/dist/css/bootstrap.min.css';

const EditProfile = ({ user }) => {
  // افتراض أن بيانات المستخدم موجودة من أعلى المستوى أو من API
  const [formData, setFormData] = useState({
    fullName: user?.fullName || '',
    email: user?.email || '',
    password: '',
    confirmPassword: '',
    role: user?.role || '',
  });

  const [isAdmin, setIsAdmin] = useState(user?.role === 'Admin');
  const [message, setMessage] = useState('');

  useEffect(() => {
    if (!user) {
      setMessage('Error: User not found');
    }
  }, [user]);

  // التعامل مع تغيير القيم في النموذج
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // إرسال البيانات بعد التعديل
  const handleSubmit = (e) => {
    e.preventDefault();
    // تحقق من تطابق كلمة المرور وتأكيد كلمة المرور
    if (formData.password !== formData.confirmPassword) {
      setMessage("Passwords don't match!");
      return;
    }

    alert('Profile updated successfully');
    // يمكن إرسال البيانات إلى الخادم هنا
  };

  return (
    <Container className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
      <Row className="w-100">
        <Col xs={12} sm={8} md={6} lg={6} className="mx-auto" >
          <Card className="shadow-lg rounded p-5" style={{ backgroundColor: '#ffffff' }}>
            <Card.Body>
              <h3 className="text-center mb-4" style={{ color: '#343a40', fontWeight: 'bold' }}>Edit Profile</h3>
              
              {/* رسالة الخطأ أو التأكيد */}
              {message && <p className="text-danger text-center">{message}</p>}

              <Form onSubmit={handleSubmit}>
                <Form.Group controlId="fullName" className="mb-3">
                  <Form.Label>Full Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="fullName"
                    value={formData.fullName}
                    onChange={handleInputChange}
                    required
                  />
                </Form.Group>

                <Form.Group controlId="email" className="mb-3">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                  />
                </Form.Group>

                {/* حقل كلمة المرور */}
                <Form.Group controlId="password" className="mb-3">
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleInputChange}
                    placeholder="Leave blank if not changing"
                  />
                </Form.Group>

                {/* حقل تأكيد كلمة المرور */}
                {formData.password && (
                  <Form.Group controlId="confirmPassword" className="mb-3">
                    <Form.Label>Confirm Password</Form.Label>
                    <Form.Control
                      type="password"
                      name="confirmPassword"
                      value={formData.confirmPassword}
                      onChange={handleInputChange}
                      placeholder="Confirm your new password"
                    />
                  </Form.Group>
                )}

                {/* عرض حقل الدور فقط للمسؤول */}
                <Form.Group controlId="role" className="mb-3">
                  <Form.Label>Role</Form.Label>
                  <Form.Control
                    type="text"
                    name="role"
                    value={formData.role}
                    readOnly
                    disabled
                  />
                </Form.Group>

                {/* إذا كان المستخدم مسؤولاً فقط */}
                {isAdmin && (
                  <div className="admin-actions mt-4">
                    <h5>Admin Actions</h5>
                    <p>You are an Admin. You can modify user roles and perform admin tasks.</p>
                    {/* يمكن إضافة المزيد من الإجراءات أو المعلومات هنا */}
                  </div>
                )}

                <Button type="submit" variant="primary" className="w-100">
                  <FaSave className="me-2" /> Save Changes
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default EditProfile;
