import React, { useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col, Card } from 'react-bootstrap';
import { FaSave, FaRegEdit } from 'react-icons/fa';
import 'bootstrap/dist/css/bootstrap.min.css';

const EditProfile = ({ user }) => {
  const [formData, setFormData] = useState({
    fullName: user.fullName,
    email: user.email,
    password: '',
    role: user.role, // في حالة المسؤول يمكن أن يسمح بتغيير الدور
  });

  const [isAdmin, setIsAdmin] = useState(user.role === 'Admin'); // تحقق إذا كان المسؤول

  // التعامل مع تغيير القيم في النموذج
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // إرسال البيانات بعد التعديل
  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Profile updated');
    // يمكن إرسال البيانات إلى الخادم هنا
  };

  return (
    <Container className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
      <Row className="w-100">
        <Col xs={12} sm={8} md={6} lg={6} className="mx-auto">
          <Card className="shadow-lg rounded p-5" style={{ backgroundColor: '#ffffff' }}>
            <Card.Body>
              <h3 className="text-center mb-4" style={{ color: '#343a40', fontWeight: 'bold' }}>Edit Profile</h3>
              <Form onSubmit={handleSubmit}>
                <Form.Group controlId="fullName" className="mb-3">
                  <Form.Label>Full Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="fullName"
                    value={formData.fullName}
                    onChange={handleInputChange}
                    required
                  />
                </Form.Group>

                <Form.Group controlId="email" className="mb-3">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                  />
                </Form.Group>

                <Form.Group controlId="password" className="mb-3">
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleInputChange}
                    placeholder="Leave blank if not changing"
                  />
                </Form.Group>

                {/* إضافة حقل الدور للمسؤول */}
                {isAdmin && (
                  <Form.Group controlId="role" className="mb-3">
                    <Form.Label>Role</Form.Label>
                    <Form.Control
                      as="select"
                      name="role"
                      value={formData.role}
                      onChange={handleInputChange}
                    >
                      <option value="Admin">Admin</option>
                      <option value="User">User</option>
                    </Form.Control>
                  </Form.Group>
                )}

                <Button type="submit" variant="primary" className="w-100">
                  <FaSave className="me-2" /> Save Changes
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default EditProfile;
