// src/pages/EditUser.js
import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom'; // لاستخدام التنقل بعد حفظ البيانات

const EditUser = () => {
  const [user, setUser] = useState({
    fullName: '',
    email: '',
    password: '',
  });

  const navigate = useNavigate();

  useEffect(() => {
    // هنا يمكنك جلب بيانات المستخدم من API أو من مصدر آخر
    const fetchedUserData = {
      fullName: 'John Doe',
      email: 'john@example.com',
      password: '',
    };
    setUser(fetchedUserData);
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  const handleSave = () => {
    // هنا يمكنك إضافة الكود لإرسال البيانات إلى API لحفظ التعديلات
    console.log('User data updated:', user);
    alert('Data updated successfully!');
    navigate('/home'); // العودة إلى الصفحة الرئيسية بعد الحفظ
  };

  return (
    <div style={{ backgroundColor: '#f7f7f7', minHeight: '100vh' }}>
      <Container className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
        <Row className="w-100">
          <Col xs={12} sm={8} md={6} lg={10} className="mx-auto">
            <Card className="shadow-sm rounded p-4">
              <Card.Body>
                <h3 className="text-center mb-4">Edit Profile</h3>

                <Form>
                  <Form.Group controlId="fullName" className="mb-3">
                    <Form.Label>Full Name</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Enter your full name"
                      name="fullName"
                      value={user.fullName}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Form.Group controlId="email" className="mb-3">
                    <Form.Label>Email</Form.Label>
                    <Form.Control
                      type="email"
                      placeholder="Enter your email"
                      name="email"
                      value={user.email}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Form.Group controlId="password" className="mb-3">
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                      type="password"
                      placeholder="Enter new password"
                      name="password"
                      value={user.password}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <div className="d-flex justify-content-center">
                    <Button variant="primary" onClick={handleSave}>
                      Save Changes
                    </Button>
                  </div>
                </Form>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default EditUser;
